import arrayimport numpy as npimport pydubfrom pydub.playback import playfrom pysndfx import AudioEffectsChainfx = AudioEffectsChain().reverb(reverberance=100).delay(delays=[750]).overdrive().compand()second = 1000minute = 60 * secondbpm = 120mpb = (1/bpm) * minutesignature = (4,4)measures = 4total_beats = measures * signature[1]total_length = mpb*total_beatstrack1 = pydub.AudioSegment.silent(duration=total_length)hihat = pydub.AudioSegment.from_wav('808/hi hat (1).wav')snare = pydub.AudioSegment.from_wav('808/snare (1).wav')kick = pydub.AudioSegment.from_wav('808/kick (11).wav')for i in range(0, int(total_length), int(mpb)):    track1 = track1.overlay(hihat, position=i)for i in range(1000, int(total_length), 4*int(mpb)):    track1 = track1.overlay(snare, position=i)for i in [0, 100, 250, 1750, 2500]:    track1 = track1.overlay(kick, position=i)samples = np.array(track1.get_array_of_samples())samples = fx(samples)samples = array.array(track1.array_type, samples)effected = track1._spawn(samples)play(effected)